def menu():
    create_image()
    game.show_long_text("Escull 1 per pasar de Celsius (ºC) a Fahrenheit (ºF)",
        DialogLayout.BOTTOM)
    game.show_long_text("Escull 2 per pasar de Fahrenheit (ºF) a Celsius (ºC)",
        DialogLayout.BOTTOM)
    opcio_usuari = game.ask_for_number("Introdueix un número", 1)
    return opcio_usuari

def redondear_numero(numero: number):
    multiplicadoPor100 = numero * 100;
    truncado = multiplicadoPor100 | 0;
    resultado = truncado / 100;
    return resultado

def salutacio(opcio: number):
    if opcio == 1:
        game.show_long_text("Introdueix graus en Celsius (ºC)", DialogLayout.BOTTOM)
        graus = game.ask_for_number("Introdueix els graus", 2)
        graus_convertits = graus * 9 / 5 + 32
        create_image_result()
        game.show_long_text("En Fahrenheit són " + str(graus_convertits) + "ºF", DialogLayout.BOTTOM)
        salutacio(menu())
    elif opcio == 2:
        game.show_long_text("Introdueix graus en Fahrenheit (ºF)", DialogLayout.BOTTOM)
        graus = game.ask_for_number("Introdueix els graus", 2)
        graus_convertits = (graus - 32) * 5 / 9
        create_image_result()
        game.show_long_text("En Celsius són " + redondear_numero(graus_convertits) + "ºC", DialogLayout.BOTTOM)
        salutacio(menu())
    else:
        game.show_long_text("Opcio Incorrecta", DialogLayout.BOTTOM)
        salutacio(menu())

